import UIKit

struct LibraryConfigurator {

	static func createModule() -> UIViewController {
        var view: LibraryViewController!

        let viewController = UIStoryboard.init(name: "Library", bundle: Bundle.main).instantiateInitialViewController()
        if viewController == nil {
            fatalError("Seems there is no initial view controller in Library.storyboard")
        }

        if viewController is UINavigationController {
            view = (viewController as! UINavigationController).viewControllers.first as? LibraryViewController
        } else {
            view = viewController as? LibraryViewController
        }

        let interactor = LibraryInteractor()
        let router = LibraryRouter()
        let presenter = LibraryPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return viewController!
    }
}
