import UIKit
import RealmSwift

class LibraryPresenter {
    // MARK: - Properties
    weak private var view: LibraryView?
    var interactor: LibraryInteractorInput?
    private let router: LibraryWireframeInterface

    // MARK: - Initialization and deinitialization -
    init(interface: LibraryView, interactor: LibraryInteractorInput?, router: LibraryWireframeInterface) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }
    func viewWillAppear() {
        self.interactor?.fetchTracksFromDB()
//        view?.displayEmptyView(animationName: "empty", title: "Список пуст", message: "Загрузите треки, чтобы прослушивать их позже")
    }

}

// MARK: - LibraryPresenterInterface -
extension LibraryPresenter: LibraryPresenterInterface {
    
}

// MARK: - LibraryInteractorOutput -
extension LibraryPresenter: LibraryInteractorOutput {
    func fetchedTracksFromDB(songs: [Songs]) {
        print (songs.map({$0.songIconUrl100}))
        self.view?.displayFetchedSongs(songs: songs)
    }
}

